mapper.select:
SELECT *
FROM (
         SELECT ROWNUM RN, A.*
         FROM (
                  SELECT *
                  FROM products_table
                  ORDER BY pno DESC
              ) A
     )
WHERE RN BETWEEN #{start} AND #{end}

mapper.update:
update products_table set p_name=#{p_name}, p_inst=#{p_inst}, p_category=#{p_category}, p_type=#{p_type},
                          p_startDate = #{p_startDate}, p_endDate=#{p_endDate}, p_price=#{p_price}
where pno = #{pno}
